mapper.select:

SELECT * FROM (
SELECT temp_table.*, ROWNUM as rnum FROM (
SELECT * FROM COMMUNITY WHERE COMMUNITY_CONTENTS_NUMBER = #{param3}
ORDER BY COMMUNITY_NUMBER DESC
) temp_table ) WHERE rnum >= #{param1} and rnum <= #{param2}


